import { useState } from 'react';

import { evaluate } from './backend.js';
function App() {
  const [calc, setCalc] = useState("");
  const [result, setResult] = useState("");

  const ops = ["+","-","/","*","."];

  const createDigits = () => {
    const digits = [];

    for (let i= 1; i<10; i++) {
      digits.push(<button key={i} onClick = {() => updateCalc(i.toString())} >{i}</button>)
    }
    return digits;
  }

  const calculate = () => {
    setCalc(evaluate(calc)).toString();
  }

  const deleteLast = () => {
    if(calc == ''){
      return;
    }

    const value = calc.slice(0, -1);
    setCalc(value);
    setResult(value);
  }

  const createOps = () => {
    const opsButt = [];

    for (let i = 0; i<4; i++) {
      opsButt.push(<button key = {ops[i]} onClick = {() => updateCalc(ops[i])}>{ops[i]}</button>)
    }

    opsButt.push(<button onClick = {deleteLast} key = {"DEL"}>{"DEL"}</button>)
    return opsButt;
  }

  return (
    <div className="App">
      <div className = "CalcDiv">
        <div className = "display">
          {result ? <span>{result}</span> : ''} 
          {calc || "0"}
        </div>

        <div className = "ops">
            {createOps()}
        </div>

        <div className = "dig">
          {createDigits()}
          <button onClick = {() => updateCalc("0")}>0</button>
          <button onClick = {() => updateCalc(".")}>.</button>
          <button onClick = {calculate}>=</button>
        </div>
      </div>
    </div>
  );
}

export default App;
