import { useState } from 'react';

function App() {
  const [calc, setCalc] = useState("");
  const [result, setResult] = useState("");

  const ops = ["+","-","/","x","DEL"];

  const updateCalc = value =>{
    setCalc(calc + value);
  }

  const createDigits = () => {
    const digits = [];

    for (let i= 1; i<10; i++) {
      digits.push(<button key={i}>{i}</button>)
    }
    return digits;
  }

  const createOps = () => {
    const opsButt = [];

    for (let i = 0; i<5; i++) {
      opsButt.push(<button key = {ops[i]} onClick = {() => updateCalc(ops[i])}>{ops[i]}</button>)
    }

    return opsButt;
  }

  return (
    <div className="App">
      <div className = "CalcDiv">
        <div className = "display">
          {result ? <span>(0)</span> : ''} 
          {calc || 0}
        </div>

        <div className = "ops">
            {createOps()}
        </div>

        <div className = "dig">
          {createDigits()}
          <button>0</button>
          <button>.</button>
          <button>=</button>
        </div>
      </div>
    </div>
  );
}

export default App;
