function App() {

  const createDigits = () => {
    const digits = [];

    for (let i= 1; i<10; i++) {
      digits.push(<button key={i}>{i}</button>)
    }
    return digits;
  }

  const createOps = () => {
    const ops = ["+","-","/","x","DEL"];
    const opsBut = [];

    for (let i = 0; i<5; i++) {
      opsBut.push(<button>{ops[i]}</button>)
    }
    return opsBut;
  }

  return (
    <div className="App">
      <div className = "CalcDiv">
        <div className = "display">
          <span>(0)</span>0
        </div>

        <div className = "ops">
            {createOps()}
        </div>

        <div className = "dig">
          {createDigits()}
          <button>0</button>
          <button>.</button>
          <button>=</button>
        </div>
      </div>
    </div>
  );
}

export default App;
